def game_winner(A):
    left, right = 0, len(A) - 1
    sequence_number = 0
    alice_turn = True
    while left <= right:
        if A[left] > sequence_number and (A[left] < A[right] or A[right] <= sequence_number):
            sequence_number = A[left]
            left += 1
        elif A[right] > sequence_number and (A[right] < A[left] or A[left] <= sequence_number):
            sequence_number = A[right]
            right -= 1
        elif A[left] > sequence_number:
            sequence_number = A[left]
            left += 1
        elif A[right] > sequence_number:
            sequence_number = A[right]
            right -= 1
        else:
            break
        alice_turn = not alice_turn
    return 'Alice' if alice_turn else 'Bob'
