def game_winner(A):
    Alice_turn = True
    left, right = 0, len(A) - 1
    last_number = -1

    while left <= right:
        if A[left] > last_number and (A[right] <= last_number or A[left] <= A[right]):
            last_number = A[left]
            left += 1
        elif A[right] > last_number and (A[left] <= last_number or A[right] < A[left]):
            last_number = A[right]
            right -= 1
        else:
            break

        Alice_turn = not Alice_turn

    return 'Alice' if Alice_turn else 'Bob'
