def beautiful_coloring(arr):
    n = max(arr)
    for d in range(2, n + 1):
        prev_color = -1
        for x in arr:
            curr_color = 0 if x % d == 0 else 1
            if curr_color == prev_color:
                break
            prev_color = curr_color
        else:
            return d
    return -1
