def polycarp_numbers(n):
    polycarp_list = []
    for i in range(1, n+1):
        if i ** 0.5 == int(i ** 0.5) or i ** (1 / 3) == int(i ** (1 / 3)):
            polycarp_list.append(i)
    return polycarp_list

