def binary_xor(str1, str2):
    length = max(len(str1), len(str2))
    str1 = str1.zfill(length)
    str2 = str2.zfill(length)

    result = ""

    for i in range(length):
        if str1[i] != str2[i]:
            result += "1"
        else:
            result += "0"

    return result.lstrip('0') or '0'
