def is_valid_date(date):
    parts = date.split('-')

    if len(parts) != 3 or not all(part.isdigit() for part in parts):
        return False

    month, day, year = map(int, parts)

    if not (1 <= month <= 12):
        return False

    if month in (4, 6, 9, 11) and day > 30:
        return False

    if month == 2:
        if ((year % 4 == 0 and year % 100 != 0) or (year % 400 == 0)):
            # Leap year
            if day > 29:
                return False
        else:
            if day > 28:
                return False

    if day > 31:
        return False

    return True
