def is_valid_date(date_string):
    import re
    from datetime import datetime

    if re.fullmatch(r'\d{1,2}-\d{1,2}-\d{4}', date_string) is None:
        return False
    else:
        try:
            datetime.strptime(date_string, '%m-%d-%Y')
            return True
        except ValueError:
            return False
