def intersection(interval1, interval2):
    lower_bound = max(interval1[0], interval2[0])
    upper_bound = min(interval1[1], interval2[1])

    if upper_bound - lower_bound > 1 or (interval1[1] == interval2[0] and interval1[1] > interval2[0]) or (interval1[0] == interval2[1] and interval1[0] < interval2[1]):
        return (lower_bound, upper_bound)
    else:
        return max(lower_bound, upper_bound)
