def interval_intersection(int1, int2):
    intersection = (max(int1[0], int2[0]), min(int1[1], int2[1]))

    if intersection[0] > intersection[1]:
        return None
    elif intersection[0] == intersection[1]:
        return intersection[0]
    else:
        return intersection  

