def convert_base(n, base):
    if base == 2:
        return bin(n).replace("0b", "0b")
    elif base == 8:
        return oct(n).replace("0o", "0")
    elif base == 16:
        return hex(n).replace("0x", "0x")
    else:
        base_n_number = ""
        while n > 0:
            n, remainder = divmod(n, base)
            base_n_number = str(remainder) + base_n_number
        return base_n_number if base_n_number else '0'
