def convert_base(num, base):
    if base == 2:
        return bin(num)
    elif base == 8:
        return oct(num)
    elif base == 16:
        return hex(num)
    else:
        alphabet = "0123456789ABCDEFGHIJKLMNOPQRSTUVWXYZ"
        if num < base:
            return alphabet[num]
        else:
            return convert_base(num // base, base) + alphabet[num % base]
