def dec_to_base(n, base):
    if base == 2:
        return bin(n).replace("0b", "")
    elif base == 8:
        return oct(n).replace("0o", "")
    elif base == 16:
        return hex(n).replace("0x", "")
    else:
        result = ""
        while n > 0:
            n, rem = divmod(n, base)
            result = str(rem) + result
        return result
