def convert_base(num, base):
    if base == 2:
        return bin(num)[2:]
    elif base == 8:
        return oct(num)[2:]
    elif base == 16:
        return hex(num)[2:]
    elif base == 10:
        return str(num)
    else:
        symbols = "0123456789ABCDEFGHIJKLMNOPQRSTUVWXYZ"
        result = ""
        while num:
            num, mod = divmod(num, base)
            result = symbols[mod] + result
        return result if result else '0'
