def convert_base(num, base):
    if base == 2:
        return bin(num)
    elif base == 8:
        return oct(num)
    elif base == 16:
        return hex(num)
    else:
        chars = '0123456789ABCDEFGHIJKLMNOPQRSTUVWXYZ'
        result = ''
        while num > 0:
            num, i = divmod(num, base)
            result = chars[i] + result
        return result
