def depth_of_nesting(s):  
    current_max = 0
    max_ = 0
    char_arr=list(s)

    for char in char_arr:
        if char == '(':
            current_max += 1
            if current_max > max_:
                max_ = current_max
        elif char == ')':
            if current_max>0:
                current_max -= 1
            else:
                return -1
    if current_max!=0:
        return -1
    return max_
