def deepest_nesting_level(s):
    current_max = 0
    max_nesting_level = 0 
    for char in s:
        if char == '(':
            current_max += 1
            if current_max > max_nesting_level:
                max_nesting_level = current_max
        elif char == ')':
            if current_max > 0:
                current_max -= 1
            else:
                return -1
    if current_max != 0: return -1
    return max_nesting_level
