def caesar_cipher(s, shift):
    shift = int(shift)
    new_str = ""

    for char in s:
        if char.isalpha():
            code = ord(char.lower()) + shift
            if code > ord('z'):
                code = ord('a') + (code - ord('z') - 1)
            new_str += chr(code)
        else:
            new_str += char

    return new_str
