def caesar_cipher(input_string, shift):
    cipher_text = ""
    for char in input_string:
        if char.isalpha():
            ascii_offset = 97 if char.islower() else 65
            cipher_char = chr((ord(char) - ascii_offset + shift) % 26 + ascii_offset)
            cipher_text += cipher_char
        else:
            cipher_text += char
    return cipher_text

