def caesar_cipher(input_string, shift_num):
    encrypted_string = ""
    for char in input_string:
        if char.isalpha():
            shift = ord(char) + int(shift_num)
            if char.islower():
                if shift > ord('z'):
                    shift -= 26
            elif char.isupper():
                if shift > ord('Z'):
                    shift -= 26
            encrypted_string += chr(shift)
        else:
            encrypted_string += char
    return encrypted_string

