def min_changes(n, m, a, b):
    p = [0]*(n+1)
    b = [int(bi) for bi in list(b)]

    for i in range(m-1, -1, -1):
        p[i] = p[i+1]*2+b[i]

    dp = [[10**9]*(n+1) for _ in range(n+1)]
    dp[0][0] = 0
    for i in range(1, n+1):
        dp[i][0] = dp[i-1][0]+int(a[i-1] == '1')

    s = 0
    for i in range(1, n+1):
        s = s*2+int(a[i-1])
        if i >= m:
            s &= (1<<m)-1
        dp[i][i] = min(dp[i][i], dp[i-1][i-1]+bin(s^p[max(0, m-i-1)]).count('1'))
        for j in range(min(i, n-m+2), 0, -1):
            dp[i][j] = min(dp[i][j], dp[i-1][j-1]+(a[i-1] != '1'))

    ans = []
    for k in range(n-m+2):
        ans.append(dp[n][k] if dp[n][k] != 10**9 else -1)

    return ans
