def count_permutations(n, m, k, p):
    factorials = [1]
    inverse_factorials = [1]

    for i in range(1, n+1):
        factorials.append((factorials[-1] * i) % p)
        inverse_factorials.append(pow(factorials[-1], p-2, p))

    dp = [[[0 for _ in range(k+1)] for _ in range(m+1)] for _ in range(n+1)]
    dp[0][0][0] = 1

    for i in range(n):
        for j in range(min(i+1, m+1)):
            for t in range(min(i, k)+1):
                good = i - j
                if dp[i][j][t] != 0:
                    dp[i+1][j][t] = (dp[i+1][j][t] + dp[i][j][t] * (j+1)) % p
                    if j+1 <= m:
                        dp[i+1][j+1][t] = (dp[i+1][j+1][t] + dp[i][j][t] * (good+1)) % p
                    if t+1 <= k and j+1 <= m:
                        dp[i+1][j+1][t+1] = (dp[i+1][j+1][t+1] + dp[i][j][t] * (j+1) * (good+1)) % p

    answer = 0
    for t in range(k, m+1):
        term = dp[n][t][k] * factorials[n]
        term = term * inverse_factorials[t]
        term = term % p
        term = term * inverse_factorials[n-t]
        answer = (answer + term) % p

    return answer

count_permutations(4, 3, 2, 10007)
