def find_x_values(arr):
    arr = [int(num) for num in arr]
    arr.sort()
    xor_limit = 0
    for num in arr:
        xor_limit |= num
    values = [0]
    for x in range(1, xor_limit + 1):
        dp = [0]*(x+1)
        dp[0] = 1
        for num in arr:
            for j in range(x, num - 1, -1):
                dp[j] |= dp[j ^ num]
        if dp[x] == 1:
            values.append(x)
    return values
