def find_x(a):
    max_val = 1 << (len(bin(max(a))) - 2)
    res = [0]

    # Helper function
    def has_subseq(a, x):
        OR = 0
        i = 0
        while (i < len(a)):
            OR |= a[i]
            if OR >= x:
                if ((x & a[i]) == x):
                    return True
            i += 1
        return False

    for x in range(1, max_val):
        if has_subseq(a, x):
            res.append(x)
    return res
