def determine_winner(A):
    last, i, j, turn = -1, 0, len(A) - 1, 0
    while i <= j:
        if min(A[i], A[j]) > last:
            last = max(A[i], A[j]) 
            if A[i] == last:
                i += 1
            else:
                j -= 1
        elif A[i] > last:
            last = A[i]
            i += 1
        elif A[j] > last:
            last = A[j]
            j -= 1
        else:
            break
        turn = 1 - turn
    if turn == 0:
        return 'Bob'
    else:
        return 'Alice'
