def determine_winner(arr):
    turn = 0
    left = 0
    right = len(arr) - 1
    last_num = -1
    while left <= right:
        if arr[left] > last_num and (arr[right] <= last_num or arr[left] >= arr[right]):
            last_num = arr[left]
            left += 1
        elif arr[right] > last_num and (arr[left] <= last_num or arr[right] > arr[left]):
            last_num = arr[right]
            right -= 1
        else:
            break
        turn += 1

    if turn % 2 == 0:
        return 'Bob'
    else:
        return 'Alice'
