def find_winner(A):
    left = 0
    right = len(A) - 1
    last = -1
    turn = False
    while (left <= right):
        if A[left] > last and (A[left] < A[right] or A[right] <= last):
            last = A[left]
            left += 1
        elif A[right] > last and (A[right] >= A[left] or A[left] <= last):
            last = A[right]
            right -= 1
        else:
            break
        turn = not turn
    return 'Alice' if turn else 'Bob'
