def find_winner(array):
    last_value = -1
    Alice_turn = True
    while len(array) > 0:
        left_value = array[0]
        right_value = array[-1]

        if left_value > last_value or right_value > last_value:
            if left_value > right_value:
                last_value = left_value
                del array[0]
            else :
                last_value = right_value
                del array[-1]
            Alice_turn = not Alice_turn
        else:
            break
    return 'Alice' if Alice_turn else 'Bob'
