def smallest_divisor(arr):
    all_divisors = []
    for i in range(len(arr)-1):
        divisor = set()
        num1, num2 = arr[i], arr[i+1]

        for j in range(1, max(num1, num2) + 1):
            if num1 % j == num2 % j == 0:
                divisor.add(j)

        all_divisors.append(list(divisor))

    common_divisors = set(all_divisors[0])
    for i in range(1, len(all_divisors)):
        common_divisors.intersection_update(all_divisors[i])

    if len(common_divisors) < 1:
        return 0
    else:
        return min(common_divisors)
