def dec_to_base(num, base):
    if base < 2 or base > 36:
        return "Invalid base"
    elif num == 0:
        return "0"
    else:
        digits = "0123456789ABCDEFGHIJKLMNOPQRSTUVWXYZ"
        result = ""
        negative = False
        if num < 0:
            negative = True
            num = abs(num)

        while num > 0:
            remainder = num % base
            result = digits[remainder] + result
            num = num // base

        if negative:
            result = "-" + result

        return result
