from functools import cmp_to_key


def asc_desc_sort(n, m, s):
    def compare(a, b):
        for i in range(m):
            if s[a - 1][i] != s[b - 1][i]:
                if i % 2 == 0:
                    return ord(s[a - 1][i]) < ord(s[b - 1][i])
                else:
                    return ord(s[a - 1][i]) > ord(s[b - 1][i])
        return 0

    return sorted(range(1, n + 1), key=cmp_to_key(compare))


if __name__ == '__main__':
    n, m = map(int, input().split())
    s = [input() for _ in range(n)]
    result = asc_desc_sort(n, m, s)
    print(*result, sep='\n')
