def min_changes(a, b):
    n = len(a)
    m = len(b)
    dp = [[0] * (n - m + 2) for _ in range(2)]
    for i in range(n - m + 1):
        dp[0][i + 1] = dp[0][i] + (a[i] != b[0])
    for i in range(1, m):
        for j in range(n - m + 1):
            dp[1][j + 1] = min(dp[0][j] + (a[i + j] != b[i]), dp[1][j] + (a[i + j] == b[i]))
        dp[0], dp[1] = dp[1], dp[0]
    return dp[0]

def main():
    n, m = map(int, input().split())
    a = input()
    b = input()
    result = min_changes(a, b)
    print(*result)

if __name__ == "__main__":
    main()
