def min_colors(n, k):
    # If k is 1, then we need 2 colors
    if k == 1:
        return 2
    # If k is 2, then we need 3 colors
    if k == 2:
        return 3
    # If k is greater than n, then we need n colors
    if k > n:
        return n
    # If k is less than n, then we need k colors
    return k
