def is_beautiful(arr, d):
    for i in range(1, len(arr)):
        if arr[i] % d == 0 and arr[i - 1] % d == 0:
            return False
    return True

def find_smallest_d(arr):
    for d in range(1, max(arr) + 1):
        if is_beautiful(arr, d):
            return d
    return 0

arr = [1, 2, 3, 4, 5, 6]
print(find_smallest_d(arr))  # Output: 2

arr = [1, 2, 3, 4, 5, 7]
print(find_smallest_d(arr))  # Output: 0 (no beautiful coloring possible)
