def count_letters(string):
  """Counts the number of occurrences of each letter in a string.

  Args:
    string: The string to count the letters in.

  Returns:
    A dictionary with the letters as keys and the number of occurrences as values.
  """

  letters = {}
  for letter in string:
    if letter not in letters:
      letters[letter] = 0
    letters[letter] += 1
  return letters
