def intersection(a, b):
    """Return the intersection of two closed intervals.

    Args:
        a (tuple): The first interval.
        b (tuple): The second interval.

    Returns:
        tuple: The intersection of the two intervals.
    """

    # Check if the intervals overlap.

    if a[1] < b[0] or b[1] < a[0]:
        return ()

    # Find the intersection.

    start = max(a[0], b[0])
    end = min(a[1], b[1])

    # Return the intersection.

    if start == end:
        return (start,)
    else:
        return (start, end)
