def to_roman(num):
    roman = ["M", "CM", "D", "CD", "C", "XC", "L", "XL", "X", "IX", "V", "IV", "I"]
    values = [1000, 900, 500, 400, 100, 90, 50, 40, 10, 9, 5, 4, 1]
    roman_num = ""
    for i in range(len(values)):
        while num >= values[i]:
            roman_num += roman[i]
            num -= values[i]
    return roman_num

