def asc_desc_sort(n, m, books):
    # Create a list of tuples, where each tuple contains the book title and its index
    books_with_indices = [(books[i], i + 1) for i in range(n)]

    # Define a custom sorting function
    def custom_sort(book1, book2):
        # Compare the odd-indexed characters in ascending order
        for i in range(0, m, 2):
            if book1[i] < book2[i]:
                return -1
            elif book1[i] > book2[i]:
                return 1

        # Compare the even-indexed characters in descending order
        for i in range(1, m, 2):
            if book1[i] > book2[i]:
                return -1
            elif book1[i] < book2[i]:
                return 1

        # If all characters are equal, return 0
        return 0

    # Sort the list of tuples using the custom sorting function
    books_with_indices.sort(key=custom_sort)

    # Return a list of integers, corresponding to the indices of the strings, starting at 1
    return [book[1] for book in books_with_indices]
