def min_changes(a, b):
    n = len(a)
    m = len(b)
    dp = [[0] * (n - m + 2) for _ in range(m + 1)]

    for i in range(m + 1):
        for k in range(n - m + 2):
            if i == 0:
                dp[i][k] = k
            elif k == 0:
                dp[i][k] = i
            else:
                if a[i - 1] == b[i - 1]:
                    dp[i][k] = dp[i - 1][k - 1]
                else:
                    dp[i][k] = min(dp[i - 1][k], dp[i][k - 1]) + 1

    return dp[-1]


