def to_roman(num):
    roman = ["M", "CM", "D", "CD", "C", "XC", "L", "XL", "X", "IX", "V", "IV", "I"]
    arabic = [1000, 900, 500, 400, 100, 90, 50, 40, 10, 9, 5, 4, 1]
    result = ""
    for i in range(len(roman)):
        while num >= arabic[i]:
            result += roman[i]
            num -= arabic[i]
    return result

