def find_overlapping(str1, str2):
    index = 0
    found = 0
    while index < len(str1):
        if str1[index:].startswith(str2):
            found += 1
            index += len(str2)
        else:
            index += 1
    return found

str1 = "ABABABC"
str2 = "AB"
print(find_overlapping(str1, str2))
