def convert_base(num, base):
  """Converts a number from decimal base to the base represented by the second number."""

  if num == 0:
    return '0'

  digits = []
  while num > 0:
    digits.append(str(num % base))
    num //= base

  return ''.join(digits[::-1])


if __name__ == '__main__':
  num = int(input('Enter a number: '))
  base = int(input('Enter a base: '))

  print(convert_base(num, base))
