def find_overlapping(string1, string2):
    index = 0
    found = 0
    while index < len(string1):
        if string1[index:].startswith(string2):
            found += 1
            index += len(string2)
        else:
            index += 1
    return found


if __name__ == "__main__":
    string1 = "ABABABA"
    string2 = "AB"
    print(find_overlapping(string1, string2))  # Output: 4
